const mongoose = require("mongoose");
const Schema = mongoose.Schema;
const moment = require("moment");

const GroupSchema = new Schema({
	groupId: {   // 
		type: String,
		trim: true,
		unique: true
	},
	channels: {
		type: Schema.Types.Mixed,
		default: []
	},
	url: { 
		type: String
	},
	profileImageUrl: { 
		type: String
	},
	addChannelArray: {
		type: Schema.Types.Mixed,
		default: []
	},
	entities: {
		type: Schema.Types.Mixed,
		default: []
	},
	addEntityArray: {
		type: Schema.Types.Mixed,
		default: []
	},
	name: {
		type: String
	},
	colors: {
		type: Schema.Types.Mixed,
		default: {}
	},
	mentions: { // number of times mentioned, by self or others
		type: Number,
		default: 0
	},
	tags: {  // i.e., channel
		type: Schema.Types.Mixed,
		default: {}
	},
	createdAt: { 
		type: Number,
		default: moment().valueOf()
	},
	lastSeen: { 
		type: Number,
		default: moment().valueOf()
	}
});

mongoose.model("Group", GroupSchema);