const mongoose = require("mongoose");
const moment = require("moment");
mongoose.Promise = global.Promise;
const Schema = mongoose.Schema;

exports.HashtagSchema = new Schema({
	nodeType: { type: String, default: "hashtag"},
	nodeId: { type: String,	unique: true },
	hashtagId: { type: String },
	text: { type: String},
	createdAt: {type: Date, default: Date.now() },
	lastSeen: {	type: Date, default: Date.now() },
	lastTweetId: { type: String },
	category: { type: String },
	categoryAuto: { type: String },
	keywords: {	type: Schema.Types.Mixed,default: {}},
	keywordsAuto: {	type: Schema.Types.Mixed, default: {} },
	isTopTerm: {type: Boolean,	default: false },
	isTrendingTopic: { type: Boolean, default: false },
	rate: {	type: Number, default: 0 },
	mentions: {	type: Number, default: 0 }
});