const mongoose = require("mongoose");
const Schema = mongoose.Schema;
mongoose.Promise = global.Promise;

exports.MediaSchema = new Schema({

	mediaId: { 
		type: String,
		unique: true
	},
	nodeId: {
		type: String
	},
	nodeType: { 
		type: String,
		default: "media"
	},
	url: {
		type: String,
		trim: true
	},
	mediaUrl: {
		type: String,
		trim: true
	},
	createdAt: {
		type: Date, default: Date.now
	},
	lastSeen: {
		type: Date, default: Date.now
	},
	lastTweetId: { type: String },
	mentions: {
		type: Number,
		default: 0
	},
	width: {
		type: Number,
		default: 1
	},
	height: {
		type: Number,
		default: 1
	}
	
});