const mongoose = require("mongoose");
mongoose.Promise = global.Promise;
const Schema = mongoose.Schema;
const moment = require("moment");

const Oauth2credentialSchema = new Schema({
	credentialType: {
		type: String
	},
	clientId: { 
		type: String,
		unique: true
	},
	clientSecret: {  
		type: String,
		trim: true
	},
	emailAddress: {  
		type: String,
		trim: true
	},
	tokenType: {
		type: String,
		trim: true
	},
	accessToken: {
		type: String,
		trim: true
	},
	refreshToken: {
		type: String,
		trim: true
	},
	expiryDate: {
		type: Date
	},
	mentions: {
		type: Number,
		default: 0
	},
	lastSeen: {
		type: Date
	}
});


mongoose.model("Oauth2credential", Oauth2credentialSchema);